import React from "react";
import { Route, Navigate } from "react-router-dom";
import { Consumer } from "./Context";

const PrivateRoute = ({ component: Component, ...rest }) => {
  return (
    <Consumer>
      {(context) => (
        <Route
          {...rest}
          render={(props) =>
            context.auth ? (
              <Component {...props} />
            ) : (
              <Navigate
                to={{
                  pathname: "/signin",
                  state: { from: props.location },
                }}
              />
            )
          }
        />
      )}
    </Consumer>
  );
};
export default PrivateRoute;
